const btnNotify = document.querySelector("#notify");
const btnActivate = document.querySelector("#activate");

const permission = Notification.requestPermission().then((permission) =>{
    if (permission !== "granted") {
        btnActivate.style.display = 'flex';
    }else{
        btnActivate.style.display = 'none';
        btnNotify.style.display = 'flex';
    }
});

var global_cooldown = localStorage.getItem('cooldown');
var global_now = new Date().getTime();
var global_remaining_time =  global_cooldown - global_now;

var global_hours = Math.floor((global_remaining_time % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
var global_minutes = Math.floor((global_remaining_time % (1000 * 60 * 60)) / (1000 * 60));
var global_seconds = Math.floor((global_remaining_time % (1000 * 60)) / 1000);

var global_hours = global_hours.toString().padStart(2,0);
var global_minutes = global_minutes.toString().padStart(2,0);
var global_seconds = global_seconds.toString().padStart(2,0);

if(global_remaining_time > 0){
    btnNotify.innerHTML = global_hours + ':' + global_minutes + ':' + global_seconds;
    btnNotify.disabled = true;
}else{
    btnNotify.innerHTML = "Notify me!";
    btnNotify.disabled = false;
}

btnNotify.onclick = ()=> {
    
    //COOLDOWN

    var date_today = new Date();

    var date_tomorrow = date_today.setDate(date_today.getDate() + 1);
    
    localStorage.setItem('cooldown', date_tomorrow);

    localStorage.setItem('visto', 0);

    //COOLDOWN

    var snd = new Audio("data:audio/wav;base64,");

    snd.play();

    navigator.serviceWorker.register('sw.js', {scope: "/"});

    navigator.serviceWorker.ready.then(function(registration) {

        registration.showNotification("Olá!", {
              body: "Aperte aqui para receber seu conselho diário.",
              icon: "https://cdn3d.iconscout.com/3d/premium/thumb/notification-bell-6335963-5220283.png",
              image: "https://cdn3d.iconscout.com/3d/premium/thumb/notification-4017936-3342630.png",
          });

    });

}

//COOLDOWN
const interval = setInterval(()=>{

    var cooldown_localstorage = localStorage.getItem('cooldown');
    var now = new Date().getTime();
    var remaining_time =  cooldown_localstorage - now;
    
    var hours = Math.floor((remaining_time % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    var minutes = Math.floor((remaining_time % (1000 * 60 * 60)) / (1000 * 60));
    var seconds = Math.floor((remaining_time % (1000 * 60)) / 1000);

    var hours = hours.toString().padStart(2,0);
    var minutes = minutes.toString().padStart(2,0);
    var seconds = seconds.toString().padStart(2,0);

    btnNotify.innerHTML = hours + ':' + minutes + ':' + seconds;

    if(remaining_time < 0){
        btnNotify.disabled = false;
        btnNotify.innerHTML = "Notify me!";
    }else{
        btnNotify.disabled = true;
        btnNotify.innerHTML = hours + ':' + minutes + ':' + seconds;
    }
    

}, 200);
//COOLDOWN